    // Copy the elements from the list to the array.
    for (int i = 0; i < list.size(); i++) {
        array[i] = list.get(i);
    }

    // Perform the sorting using the TimSort algorithm.
    int minIndex = 0;
    int minElement = array[minIndex];
    for (int i = 1; i < array.length; i++) {
        int element = array[i];
        if (comparator.compare(element, minElement) < 0) {
            minElement = element;
            minIndex = i;
        }
    }

    // Swap the minimum element with the first element in the array.
    array[minIndex] = minElement;
    for (int j = minIndex + 1; j < array.length; j++) {
